script:
frappe.ui.form.on('Purchase Order', {
  supplier(frm) {
    if (!frm.doc.supplier) return;
    frappe.db.get_value('Supplier',
      frm.doc.supplier,
      'custom_default_incoterm',
      (r) => {
        if (r.custom_default_incoterm) {
          frm.set_value('incoterm', r.custom_default_incoterm);
        }
        else {
          frm.set_value('incoterm', '');
        }

      }
    );
  }
});
