css:
@page {
    margin-top: 15mm;
    margin-right: 10mm;
    margin-bottom: 15mm;
    margin-left: 10mm;
}

@media print {
    /* Keep print-format margin at 0; rely on @page for real PDF margins */
    .print-format {
      margin: 0 !important;
      padding: 0 !important;

    }

    /* Optional: prevent “first element sticks to top” effects */
    .print-heading { margin-top: 0; }

}
