css:
@media all {
  .print-format {
       /*refert to this measurement in the file frappe/utils/pdf.py line 130 - use margin in this way*/
        padding: 0.0cm;
        margin-left:0mm;
        margin-top:0mm;
        margin-right:0mm;
        margin-bottom:0mm;
        /*box-shadow: 0px 0px 9px rgba(0,0,0,0.5);*/
  }
}
table.label-table {
width: 100%;
border-collapse: collapse;
font-family: Arial, sans-serif;
font-size: 8pt;
page-break-inside: avoid;
}

table.label-table td {
padding: 0.4mm 0.6mm;
vertical-align: top;
}

.label-header {
font-size: 8.5pt;
font-weight: bold;
border-bottom: 1px solid #000;
}

.label-title {
text-align: right;
font-weight: bold;
text-transform: uppercase;
}

.item-code {
font-size: 11pt;
font-weight: bold;
}

.item-name {
font-size: 7pt;
margin-bottom: 0.5mm;
}

.inner-table {
width: 100%;
border-collapse: collapse;
font-size: 7pt;
}

.inner-table td {
padding: 0.2mm 0;
vertical-align: top;
}

.small-label {
font-weight: bold;
white-space: nowrap;
padding-right: 1mm;
}

.qr-cell {
text-align: center;
page-break-inside: avoid;
}

.qr-cell img {
width: 24mm;
height: 24mm;
}

.qr-text {
font-size: 6.5pt;
margin-top: 0.5mm;
word-break: break-all;
}

.footer-row {
border-top: 1px solid #000;
font-size: 6.5pt;
}
