css:
@media all {
  .print-format {
    padding: 0.0cm;
    margin: 5mm;
  }
}

.label-table {
  width: 62mm;
  height: 85mm;
  line-height: 1;
}

.company-header {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
}

.label-title {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
  padding: 0 0 1mm 0;
  border-bottom: 0.3mm solid #000;
  margin-bottom: 1mm;
}

.item-code {
  font-size: 5mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.4px black;
  letter-spacing: 0.02em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
}

.item-name {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin-left: 0mm;
  text-align: left;
}

.details-grid {
  display: grid;
  grid-template-columns: auto 1fr;
  column-gap: 2mm;
  row-gap: 0.5mm;
  font-size: 3mm;
  font-family: "Lucida Console", "Courier New", monospace;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.04em;
  transform: scaleX(1.05);
  transform-origin: left;
  margin-top: 1mm;
  margin-bottom: 2mm;
}

.small-label {
  text-align: left;
  white-space: nowrap;
}

.detail-value {
  text-align: left;
}

.qr-cell {
  width: 30mm;
  height: 30mm;
  text-align: center;
}

img {
  width: 30mm;
  height: 30mm;
}

.action-banner {
  display: none;
}
