css:
@media all {
  .print-format {
    padding: 0.0cm;
    margin: 5mm;
  }
}

.label-table {
  width: 62mm;
  height: 85mm;
  line-height: 1;
}

.company-header {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
}

.label-title {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
  padding: 0 0 1mm 0;
  border-bottom: 0.3mm solid #000;
  margin-bottom: 1mm;
}

.item-code {
  font-size: 5mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.4px black;
  letter-spacing: 0.02em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin: 0mm;
  text-align: left;
}

.item-name {
  font-size: 3mm;
  font-family: Calibri, Arial, sans-serif;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.03em;
  transform: scaleX(1.03);
  transform-origin: left;
  margin-left: 0mm;
  text-align: left;
}

.details-grid {
  display: grid;
  grid-template-columns: auto 1fr;
  column-gap: 2mm;
  row-gap: 0.5mm;
  font-size: 3mm;
  font-family: "Lucida Console", "Courier New", monospace;
  font-weight: 700;
  -webkit-text-stroke: 0.3px black;
  letter-spacing: 0.04em;
  transform: scaleX(1.05);
  transform-origin: left;
  margin-top: 1mm;
  margin-bottom: 2mm;
}

.small-label {
  text-align: left;
  white-space: nowrap;
}

.detail-value {
  text-align: left;
}

.qr-cell {
  width: 30mm;
  height: 30mm;
  text-align: center;
}

img {
  width: 30mm;
  height: 30mm;
}

.action-banner {
  display: none;
}

html:
{% set fd_item_code = frappe.form_dict.get('item_code') %}
{% set ns = namespace(item_code=None, item_name=None, batch_no=None, uom=None, qty=None) %}

{% if fd_item_code %}
  {% set ns.item_code = fd_item_code %}
  {% set ns.item_name = frappe.form_dict.get('item_name') or '' %}
  {% set ns.batch_no = frappe.form_dict.get('batch_no') or '' %}
  {% set ns.uom = frappe.form_dict.get('uom') or '' %}
  {% set ns.qty = frappe.form_dict.get('qty') or '0' %}
{% else %}
  {% set row_name = frappe.form_dict.get('row_name') %}
  {% set row_ns = namespace(row=None) %}
  {% if doc.items %}
    {% if row_name %}
      {% for r in doc.items %}
        {% if r.name == row_name %}
          {% set row_ns.row = r %}
        {% endif %}
      {% endfor %}
    {% endif %}
    {% if not row_ns.row %}
      {% set row_ns.row = doc.items[0] %}
    {% endif %}
  {% endif %}
  {% if row_ns.row %}
    {% set ns.item_code = row_ns.row.item_code %}
    {% set ns.item_name = row_ns.row.item_name or frappe.db.get_value("Item", row_ns.row.item_code, "item_name") %}
    {% set ns.batch_no = row_ns.row.batch_no or '' %}
    {% set ns.uom = row_ns.row.uom or row_ns.row.stock_uom or '' %}
    {% set ns.qty = row_ns.row.qty %}
  {% endif %}
{% endif %}

<!-- Header -->
<div class="label-title">Pallet Label (Collective)</div>

{% if ns.item_code %}
{% set qr_payload = ns.item_code ~ '|' ~ (ns.batch_no or '') ~ '|' ~ ns.qty %}

<!-- Main content row: text on left, QR on right -->
    <div class="item-code">{{ ns.item_code }}</div>
    <div class="item-name">{{ ns.item_name or '' }}</div>

    <div class="details-grid">
        <div class="small-label">Qty</div>
        <div class="detail-value">{{ ns.qty }} {{ ns.uom or '' }}</div>
        <div class="small-label">Batch</div>
        <div class="detail-value">{{ ns.batch_no or '-' }}</div>
    </div>

    <div class="qr-cell">
    <img src="{{ get_qr_code(qr_payload, scale=3) }}" alt="QR">
    </div>
{% endif %}
